	responseContent = testRunner.testCase.getTestStepByName("getAccounts").getPropertyValue("response")

	jsonParser = new JsonSlurper().parseText(responseContent)

	def accountId=jsonParser.body[0].accountId

	def accountIds=""

	for (int j=0; j<jsonParser.body.size(); j++){

		accountId=jsonParser.body[j].accountId

		def tsteps = testRunner.testCase.getTestStepByName("getTransactions")

		context.testCase.setPropertyValue("accountId", accountId)


		def status = tsteps.run(testRunner, context)

		header = testRunner.testCase.testSteps['getTransactions'].testRequest.response.getResponseHeaders()


		if (header["#status#"] == ['HTTP/1.1 200 OK']){

			if (accountIds.length() == 0){

				accountIds = accountId
			}
			else

			accountIds = accountIds + ";"+ accountId

		}


		sleep (100)

		}


	if (accountIds.length() >0) {

		log.info (accountIds)

		i = custonerJsonParser.body.size()
	}

