import groovy.json.JsonSlurper

customerResponseContent = testRunner.testCase.getTestStepByName("getCustomers").getPropertyValue("response")

custonerJsonParser = new JsonSlurper().parseText(customerResponseContent)

def customerId=custonerJsonParser.body[0].accountId

def tstep = testRunner.testCase.getTestStepByName("getPortfolioOverdueSettlements")

for (int i=0; i<custonerJsonParser.body.size(); i++){

	customerId=custonerJsonParser.body[i].customerId

	context.testCase.setPropertyValue("customerId", customerId)

	tstep.run(testRunner, context)

	header = testRunner.testCase.testSteps['getPortfolioOverdueSettlements'].testRequest.response.getResponseHeaders()

	if (header["#status#"] == ['HTTP/1.1 404 Not Found']){
		continue
	}

	responseContent = testRunner.testCase.getTestStepByName("getPortfolioOverdueSettlements").getPropertyValue("response")

	jsonParser = new JsonSlurper().parseText(responseContent)

	if (jsonParser.body != null && jsonParser.body.size() > 0)
	{
		log.info (customerId)
	}
	sleep (100)
}
