	responseContent = testRunner.testCase.getTestStepByName("getInstrumentId").getPropertyValue("response")

	jsonParser = new JsonSlurper().parseText(responseContent)

	def instrumentId=jsonParser.body[0].instrumentId

	def instrumentIds=""

	for (int j=0; j<jsonParser.body.size(); j++){

		instrumentId=jsonParser.body[j].instrumentId

		def tsteps = testRunner.testCase.getTestStepByName("getInstrumentPositions")

		context.testCase.setPropertyValue("instrumentId", instrumentId)


		def status = tsteps.run(testRunner, context)

		header = testRunner.testCase.testSteps['getInstrumentPositions'].testRequest.response.getResponseHeaders()


		if (header["#status#"] == ['HTTP/1.1 200 OK']){

			if (instrumentIds.length() == 0){

				instrumentIds = instrumentId
			}
			else

			instrumentIds = instrumentIds + ";"+ instrumentId

		}


		sleep (100)

		}


	if (instrumentIds.length() >0) {

		log.info (instrumentIds)

		i = custonerJsonParser.body.size()
	}

//}

import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("getCurrencyPairs").getPropertyValue("response")
jsonParser = new JsonSlurper().parseText(responseContent)

def accountId=jsonParser.body[0].currencyPair

def accountIds=""

for (int i=0; i<jsonParser.body.size(); i++){

accountId=jsonParser.body[i].currencyPair

def tstep = testRunner.testCase.getTestStepByName("getCurrencyPairPositions");

context.testCase.setPropertyValue("currencyPair", accountId)


def status = tstep.run(testRunner, context)

header = testRunner.testCase.testSteps['getCurrencyPairPositions'].testRequest.response.getResponseHeaders()


if (header["#status#"] == ['HTTP/1.1 200 OK']){

	if (accountIds.length() == 0){

		accountIds = accountId
	}
	else

	accountIds = accountIds + ";"+ accountId
	//log.info (i)
}

sleep (100)

}

if (accountIds.length() >0) {
	log.info (accountIds)
}
