	responseContent = testRunner.testCase.getTestStepByName("getTables").getPropertyValue("response")

	jsonParser = new JsonSlurper().parseText(responseContent)

	def tableId=jsonParser.body[0].tableId

	def tableIds=""

	for (int j=0; j<jsonParser.body.size(); j++){

		tableId=jsonParser.body[j].tableId

		def tsteps = testRunner.testCase.getTestStepByName("getVirtualTableByName")

		context.testCase.setPropertyValue("tableId", tableId)


		def status = tsteps.run(testRunner, context)

		header = testRunner.testCase.testSteps['getVirtualTableByName'].testRequest.response.getResponseHeaders()


		if (header["#status#"] == ['HTTP/1.1 200 OK']){

			if (tableIds.length() == 0){

				tableIds = tableId
			}
			else

			tableIds = tableIds + ","+ tableId

		}


		sleep (100)

		}


	if (tableIds.length() >0) {

		log.info (tableIds)

