con:config.script:
import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("getAccounts").getPropertyValue("response")
jsonParser = new JsonSlurper().parseText(responseContent)

def accountId=jsonParser.body[0].accountId

def accountIds=""

for (int i=0; i<jsonParser.body.size(); i++){

accountId=jsonParser.body[i].accountId

def tstep = testRunner.testCase.getTestStepByName("getAccountPaymentList");

context.testCase.setPropertyValue("accountId", accountId)


def status = tstep.run(testRunner, context)

header = testRunner.testCase.testSteps['getAccountPaymentList'].testRequest.response.getResponseHeaders()


if (header["#status#"] == ['HTTP/1.1 200 OK']){

	if (accountIds.length() == 0){

		accountIds = accountId
	}
	else

	accountIds = accountIds + ";"+ accountId
	//log.info (i)
}

sleep (100)

}

if (accountIds.length() >0) {
	log.info (accountIds)
}
