import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("getCustomers").getPropertyValue("response")
jsonParser = new JsonSlurper().parseText(responseContent)

def customerId=jsonParser.body[0].customerId

String customerIds=""

for (int i=0; i<jsonParser.body.size(); i++){

customerId=jsonParser.body[i].customerId

def tstep = testRunner.testCase.getTestStepByName("getImages");

context.testCase.setPropertyValue("customerId", customerId)


def status = tstep.run(testRunner, context)

header = testRunner.testCase.testSteps['getImages'].testRequest.response.getResponseHeaders()

if (header["#status#"] == ['HTTP/1.1 200 OK']){

	if (customerIds.length() == 0){

		customerIds = customerId
	}
	else

	customerIds = customerIds + ";"+ customerId
}

sleep (100)

}

if (customerIds.length() >0) {
	log.info (customerIds)
}

