con:config.script:
//Groovy - run request from another TestCase or TestSuite

import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("getPendingRepairOrders").getPropertyValue("response")
jsonParser = new JsonSlurper().parseText(responseContent)

def paymentId=jsonParser.body[0].paymentId

for (int i=0; i<jsonParser.body.size(); i++){

	paymentId=jsonParser.body[i].paymentId

	def tstep = testRunner.testCase.getTestStepByName("getPaymentIdPaymentStatus");

	context.testCase.setPropertyValue("paymentId", paymentId)


	def status = tstep.run(testRunner, context)

	header = testRunner.testCase.testSteps['getPaymentIdPaymentStatus'].testRequest.response.getResponseHeaders()

	if (header["#status#"] == ['HTTP/1.1 200 OK']){

		log.info (paymentId)
		break
	}

	sleep (100)

}
